<?phpif ( ! defined( 'WP_UNINSTALL_PLUGIN' ) )	exit();	function dbp_delete_plugin(){	remove_role( 'seller' );	remove_role( 'client' );	remove_role( 'auditor' );	remove_role( 'manager' );	// Leer datos de tienda	delete_option("dbp_shop_name");	delete_option("dbp_shop_nickname");	delete_option("dbp_shop_rif");	delete_option("dbp_shop_tlf_local");	delete_option("dbp_shop_tlf_mobile");	delete_option("dbp_shop_addr");		// Leer datos comerciales	delete_option("dbp_active_sales");	delete_option("dbp_currency");	delete_option("dbp_currency_symbol");	delete_option("dbp_shop_tax_n");	delete_option("dbp_shop_tax_v");		// Leer datos bancarios	delete_option("dbp_active_bank_transfer");	delete_option("dbp_account_holder");	delete_option("dbp_account_holder_document");	delete_option("dbp_account_bank");	delete_option("dbp_account_number");	delete_option("dbp_account_holder_email");	$posts = get_posts( array(		'numberposts' => -1,		'post_type' => 'dbp_invoice',		'post_status' => 'any' ) );	foreach ( $posts as $post )		wp_delete_post( $post->ID, true );}dbp_delete_plugin();